        parseTrade (trade, market = undefined) {
            //
            // public fetchTrades
            //
            //     {
            //         "p":"9395.50000000",
            //         "q":"50.000000",
            //         "t":1592563996718
            //     }
            //
            // private fetchMyTrades
            //
            //     {
            //         "avgPrice":"1199.8",
            //         "base":"ETH",
            //         "clOrdID":null,
            //         "commission":"0.00002",
            //         "createTime":"2021-01-11T02:47:51.512Z",
            //         "cumQty":"0.02",
            //         "filledOrderID":"1eUD4F5rwK",
            //         "filledPrice":"1199.8",
            //         "filledQty":"0.02",
            //         "leavesQty":"0",
            //         "oCreateTime":"2021-01-11T02:47:51.377Z",
            //         "orderID":"1eUD4EHfdU",
            //         "orderQty":"0.02",
            //         "orderStatus":3,
            //         "orderType":1,
            //         "price":"1198.25",
            //         "quote":"USDT",
            //         "rejectCode":null,
            //         "rejectReason":null,
            //         "side":1,
            //         "stopPrice":"0",
            //         "symbol":"ETHUSDT",
            //         "taker":true,
            //         "tradeID":"E04WTIgfmULU",
            //         "transactTime":"2021-01-11T02:47:51.389Z",
            //         "updateTime":null,
            //         "userID":"1362494"
            //     }
            //
            let timestamp = this.safeInteger (trade, 't');
            if (timestamp === undefined) {
                timestamp = this.parse8601 (this.safeString (trade, 'createTime'));
            }
            const id = this.safeString2 (trade, 'tid', 'tradeID');
            let symbol = undefined;
            const marketId = this.safeString (trade, 'symbol');
            market = this.safeMarket (marketId, market);
            if (market !== undefined) {
                symbol = market['symbol'];
            }
            let priceString = this.safeString2 (trade, 'p', 'filledPrice');
            const amountString = this.safeString2 (trade, 'q', 'filledQty');
            const orderId = this.safeString (trade, 'orderID');
            const isTaker = this.safeValue (trade, 'taker');
            let takerOrMaker = undefined;
            if (isTaker !== undefined) {
                takerOrMaker = isTaker ? 'taker' : 'maker';
            }
            let side = this.safeString (trade, 'side');
            if (side === '1') {
                side = 'buy';
            } else if (side === '2') {
                side = 'sell';
            }
            if (side === undefined) {
                side = (priceString[0] === '-') ? 'sell' : 'buy';
            }
            priceString = Precise.stringAbs (priceString);
            const price = this.parseNumber (priceString);
            const amount = this.parseNumber (amountString);
            const cost = this.parseNumber (Precise.stringMul (priceString, amountString));
            const orderType = this.parseOrderType (this.safeString (trade, 'orderType'));
            let fee = undefined;
            const feeCost = this.safeNumber (trade, 'commission');
            if (feeCost !== undefined) {
                let feeCurrency = undefined;
                if (market !== undefined) {
                    if (side === 'buy') {
                        feeCurrency = market['base'];
                    } else if (side === 'sell') {
                        feeCurrency = market['quote'];
                    }
                }
                fee = {
                    'currency': feeCurrency,
                    'cost': feeCost,
                };
            }
            return {
                'info': trade,
                'id': id,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'symbol': symbol,
                'type': orderType,
                'side': side,
                'order': orderId,
                'takerOrMaker': takerOrMaker,
                'price': price,
                'amount': amount,
                'cost': cost,
                'fee': fee,
            };
        }
    
