            //                 "qtyScale":1,
            //                 "priceScale":8,
            //                 "notionalScale":4
            //             }
            //         ]
            //     }
            //
            const futures = await this.publicGetFuturesContracts (params);
            //
            //     {
            //         "code":0,
            //         "data":[
            //             {
            //                 "symbol":"BTC-PERP",
            //                 "tradingStartTime":1579701600000,
            //                 "collapseDecimals":"1,0.1,0.01",
