            //         "cumFee":       "0.005152238",
            //         "cumFilledQty": "0.00082",
            //         "errorCode":    "",
            //         "feeAsset":     "USDT",
            //         "lastExecTime": 1575953151764,
            //         "orderId":      "a16eee20b6750866943712zWEDdAjt3",
            //         "seqNum":       2623469,
            //         "side":         "Buy",
            //         "status":       "Filled",
            //         "stopPrice":    "",
            //         "execInst":     "NULL_VAL"
            //     }
            //
            //     {
