            if (limit !== undefined) {
                request['limit_bids'] = limit;
                request['limit_asks'] = limit;
            }
            const response = await this.publicGetBookSymbol (this.extend (request, params));
            return this.parseOrderBook (response, symbol, undefined, 'bids', 'asks', 'price', 'amount');
