        parseMarket (market) {
            //
            // spot
            //
            //     {
            //         "base_currency":"btc",
            //         "quote_currency":"usdt",
            //         "symbol":"btc_usdt",
            //         "tick_size":"2",
            //         "size_increment":"4",
            //         "status":"1",
            //         "base_asset_precision":"8"
            //     }
            //
            //
            // swap
            //
            //     {
            //         "symbol":"btcusd",
            //         "underlying_index":"BTC",
            //         "quote_currency":"USD",
            //         "coin":"BTC",
            //         "contract_val":"1",
            //         "listing":null,
            //         "delivery":["07:00:00","15:00:00","23:00:00"],
            //         "size_increment":"0",
            //         "tick_size":"1",
            //         "forwardContractFlag":false,
            //         "priceEndStep":5
            //     }
            //
            const id = this.safeString (market, 'symbol');
            let marketType = 'spot';
            let spot = true;
            let swap = false;
            const baseId = this.safeString2 (market, 'base_currency', 'coin');
            const quoteId = this.safeString (market, 'quote_currency');
            const contractVal = this.safeNumber (market, 'contract_val');
            if (contractVal !== undefined) {
                marketType = 'swap';
                spot = false;
                swap = true;
            }
            const base = this.safeCurrencyCode (baseId);
            const quote = this.safeCurrencyCode (quoteId);
            let symbol = id.toUpperCase ();
            if (spot) {
                symbol = base + '/' + quote;
            }
            const tickSize = this.safeString (market, 'tick_size');
            const sizeIncrement = this.safeString (market, 'size_increment');
            const precision = {
                'amount': this.parseNumber (this.parsePrecision (sizeIncrement)),
                'price': this.parseNumber (this.parsePrecision (tickSize)),
            };
            const minAmount = this.safeNumber2 (market, 'min_size', 'base_min_size');
            const status = this.safeString (market, 'status');
            let active = undefined;
            if (status !== undefined) {
                active = (status === '1');
            }
            const fees = this.safeValue2 (this.fees, marketType, 'trading', {});
            return this.extend (fees, {
                'id': id,
                'symbol': symbol,
                'base': base,
                'quote': quote,
                'baseId': baseId,
                'quoteId': quoteId,
                'info': market,
                'type': marketType,
                'spot': spot,
                'swap': swap,
                'active': active,
                'precision': precision,
                'limits': {
                    'amount': {
                        'min': minAmount,
                        'max': undefined,
                    },
                    'price': {
                        'min': precision['price'],
                        'max': undefined,
                    },
                    'cost': {
                        'min': precision['price'],
                        'max': undefined,
                    },
                },
            });
        }
    
