            //     {
            //         "order_id":1736871726781,
            //         "symbol":"BTC_USDT",
            //         "create_time":1591096004000,
            //         "side":"sell",
            //         "type":"market",
            //         "price":"0.00",
            //         "price_avg":"0.00",
            //         "size":"0.02000",
            //         "notional":"0.00000000",
            //         "filled_notional":"0.00000000",
            //         "filled_size":"0.00000",
            //         "status":"8"
            //     }
            //
            // contract fetchOrder, fetchOrdersByStatus, fetchOpenOrders, fetchClosedOrders, fetchOrders
            //
            //     {
            //         "order_id": 10539098,
            //         "contract_id": 1,
            //         "position_id": 10539088,
            //         "account_id": 10,
            //         "price": "16",
            //         "vol": "1",
            //         "done_avg_price": "16",
            //         "done_vol": "1",
            //         "way": 3,
            //         "category": 1,
            //         "open_type": 2,
            //         "make_fee": "0.00025",
            //         "take_fee": "0.012",
            //         "origin": "",
            //         "created_at": "2018-07-23T11:55:56.715305Z",
            //         "finished_at": "2018-07-23T11:55:56.763941Z",
            //         "status": 4,
            //         "errno": 0
            //     }
            //
            let id = undefined;
