            // spot and contract
            //
            //     {
            //         "code": 1000,
            //         "trace":"886fb6ae-456b-4654-b4e0-d681ac05cea1",
            //         "message": "OK",
            //         "data": {
            //             "order_id": 2707217580
            //         }
