        parseTrade (trade, market = undefined) {
            //
            // fetchTrades (public)
            //
            //     {
            //         "id":"94154c98-6e8b-4e33-92a8-74e33fc05650",
            //         "timestamp":1590382761859,
            //         "amount":"0.06026079",
            //         "price":"8095.3",
            //         "side":"buy"
            //     }
            //
            // createOrder, fetchOpenOrders, fetchOrders, editOrder (private)
            //
            //     {
            //         "id":"b0c86aa5-6ed3-4a2d-ba3a-be9a964220f4",
            //         "timestamp":1590505649245,
            //         "amount":"0.249825",
            //         "price":"183.49",
            //         "taker":true,
            //         "fee":"0.12038925",
            //         "feeCurrency":"EUR",
            //         "settled":true
            //     }
            //
            // fetchMyTrades (private)
            //
            //     {
            //         "id":"b0c86aa5-6ed3-4a2d-ba3a-be9a964220f4",
            //         "orderId":"af76d6ce-9f7c-4006-b715-bb5d430652d0",
            //         "timestamp":1590505649245,
            //         "market":"ETH-EUR",
            //         "side":"sell",
            //         "amount":"0.249825",
            //         "price":"183.49",
            //         "taker":true,
            //         "fee":"0.12038925",
            //         "feeCurrency":"EUR",
            //         "settled":true
            //     }
            //
            // watchMyTrades (private)
            //
            //     {
            //         event: 'fill',
            //         timestamp: 1590964470132,
            //         market: 'ETH-EUR',
            //         orderId: '85d082e1-eda4-4209-9580-248281a29a9a',
            //         fillId: '861d2da5-aa93-475c-8d9a-dce431bd4211',
            //         side: 'sell',
            //         amount: '0.1',
            //         price: '211.46',
            //         taker: true,
            //         fee: '0.056',
            //         feeCurrency: 'EUR'
            //     }
            //
            const priceString = this.safeString (trade, 'price');
            const amountString = this.safeString (trade, 'amount');
            const price = this.parseNumber (priceString);
            const amount = this.parseNumber (amountString);
            const cost = this.parseNumber (Precise.stringMul (priceString, amountString));
            const timestamp = this.safeInteger (trade, 'timestamp');
            const side = this.safeString (trade, 'side');
            const id = this.safeString2 (trade, 'id', 'fillId');
            const marketId = this.safeInteger (trade, 'market');
            const symbol = this.safeSymbol (marketId, market, '-');
            const taker = this.safeValue (trade, 'taker');
            let takerOrMaker = undefined;
            if (taker !== undefined) {
                takerOrMaker = taker ? 'taker' : 'maker';
            }
            const feeCost = this.safeNumber (trade, 'fee');
            let fee = undefined;
            if (feeCost !== undefined) {
                const feeCurrencyId = this.safeString (trade, 'feeCurrency');
                const feeCurrencyCode = this.safeCurrencyCode (feeCurrencyId);
                fee = {
                    'cost': feeCost,
                    'currency': feeCurrencyCode,
                };
            }
            const orderId = this.safeString (trade, 'orderId');
            return {
                'info': trade,
                'id': id,
                'symbol': symbol,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'order': orderId,
                'type': undefined,
                'side': side,
                'takerOrMaker': takerOrMaker,
                'price': price,
                'amount': amount,
                'cost': cost,
                'fee': fee,
            };
        }
    
