        parseTicker (ticker, market = undefined) {
            //
            //       {    instrument: "ZZC-USD",
            //                  last:  0.065,
            //         percentChange:  0,
            //                   low:  0.065,
            //                  high:  0.065,
            //            baseVolume:  0,
            //           quoteVolume:  0,
            //           volumeInBtc:  0,
            //           volumeInUsd:  0,
            //                   ask:  0.5,
            //                   bid:  0.0007,
            //             timestamp: "2018-10-31T09:21:25Z" }   ]
            //
            const timestamp = this.parse8601 (this.safeString (ticker, 'timestamp'));
            const marketId = this.safeString (ticker, 'instrument');
            const symbol = this.safeSymbol (marketId, market, '-');
            const last = this.safeNumber (ticker, 'last');
            return {
                'symbol': symbol,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'high': this.safeNumber (ticker, 'high'),
                'low': this.safeNumber (ticker, 'low'),
                'bid': this.safeNumber (ticker, 'bid'),
                'bidVolume': undefined,
                'ask': this.safeNumber (ticker, 'ask'),
                'askVolume': undefined,
                'vwap': undefined,
                'open': undefined,
                'close': last,
                'last': last,
                'previousClose': undefined, // previous day close
                'change': undefined,
                'percentage': this.safeNumber (ticker, 'percentChange'),
                'average': undefined,
                'baseVolume': this.safeNumber (ticker, 'baseVolume'),
                'quoteVolume': this.safeNumber (ticker, 'quoteVolume'),
                'info': ticker,
            };
        }
    
        async fetchTicker (symbol, params = {}) {
            await this.loadMarkets ();
            const market = this.market (symbol);
            const request = {
                'instrument': market['id'],
            };
            const response = await this.publicGetTickers (this.extend (request, params));
            //
            //     [ {    instrument: "$PAC-BTC",
            //                  last:  3.3e-7,
            //         percentChange:  3.125,
            //                   low:  2.7e-7,
            //                  high:  3.3e-7,
            //            baseVolume:  191700.79823187,
            //           quoteVolume:  0.0587930939346704,
            //           volumeInBtc:  0.0587930939346704,
            //           volumeInUsd:  376.2006339435353,
            //                   ask:  3.3e-7,
            //                   bid:  3.1e-7,
            //             timestamp: "2018-10-31T09:21:25Z" }   ]
            //
            const numTickers = response.length;
            if (numTickers < 1) {
                throw new ExchangeError (this.id + ' fetchTicker could not load quotes for symbol ' + symbol);
            }
            return this.parseTicker (response[0], market);
        }
    
