        parseTrade (trade, market = undefined) {
            //
            // fetchTrades (public)
            //
            //     {
            //         "trade_id":165087520,
            //         "date":1587470005,
            //         "type":"buy",
            //         "quantity":"1.004",
            //         "price":"0.02491461",
            //         "amount":"0.02501426"
            //     },
            //
            // fetchMyTrades, fetchOrderTrades
            //
            //     {
            //         "trade_id": 3,
            //         "date": 1435488248,
            //         "type": "buy",
            //         "pair": "BTC_USD",
            //         "order_id": 12345,
            //         "quantity": 1,
            //         "price": 100,
            //         "amount": 100,
            //         "exec_type": "taker",
            //         "commission_amount": "0.02",
            //         "commission_currency": "BTC",
            //         "commission_percent": "0.2"
            //     }
            //
            const timestamp = this.safeTimestamp (trade, 'date');
            let symbol = undefined;
            const id = this.safeString (trade, 'trade_id');
            const orderId = this.safeString (trade, 'order_id');
            const price = this.safeNumber (trade, 'price');
            const amount = this.safeNumber (trade, 'quantity');
            const cost = this.safeNumber (trade, 'amount');
            const side = this.safeString (trade, 'type');
            const type = undefined;
            const marketId = this.safeString (trade, 'pair');
            if (marketId !== undefined) {
                if (marketId in this.markets_by_id) {
                    market = this.markets_by_id[marketId];
                } else {
                    const [ baseId, quoteId ] = marketId.split ('_');
                    const base = this.safeCurrencyCode (baseId);
                    const quote = this.safeCurrencyCode (quoteId);
                    symbol = base + '/' + quote;
                }
            }
            if ((symbol === undefined) && (market !== undefined)) {
                symbol = market['symbol'];
            }
            const takerOrMaker = this.safeString (trade, 'exec_type');
            let fee = undefined;
            const feeCost = this.safeNumber (trade, 'commission_amount');
            if (feeCost !== undefined) {
                const feeCurrencyId = this.safeString (trade, 'commission_currency');
                const feeCurrencyCode = this.safeCurrencyCode (feeCurrencyId);
                let feeRate = this.safeNumber (trade, 'commission_percent');
                if (feeRate !== undefined) {
                    feeRate /= 1000;
                }
                fee = {
                    'cost': feeCost,
                    'currency': feeCurrencyCode,
                    'rate': feeRate,
                };
            }
            return {
                'id': id,
                'info': trade,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'symbol': symbol,
                'order': orderId,
                'type': type,
                'side': side,
                'takerOrMaker': takerOrMaker,
                'price': price,
                'amount': amount,
                'cost': cost,
                'fee': fee,
            };
        }
    
