        parseOrder (order, market = undefined) {
            //
            // fetchOrders, fetchOpenOrders, fetchClosedOrders
            //
            //     {
            //         "order_id": "14",
            //         "created": "1435517311",
            //         "type": "buy",
            //         "pair": "BTC_USD",
            //         "price": "100",
            //         "quantity": "1",
            //         "amount": "100"
            //     }
            //
            // fetchOrder
            //
            //     {
            //         "type": "buy",
            //         "in_currency": "BTC",
            //         "in_amount": "1",
            //         "out_currency": "USD",
            //         "out_amount": "100",
            //         "trades": [
            //             {
            //                 "trade_id": 3,
            //                 "date": 1435488248,
            //                 "type": "buy",
            //                 "pair": "BTC_USD",
            //                 "order_id": 12345,
            //                 "quantity": 1,
            //                 "price": 100,
            //                 "amount": 100
            //             }
            //         ]
            //     }
            //
            let id = this.safeString (order, 'order_id');
            let timestamp = this.safeTimestamp (order, 'created');
            let symbol = undefined;
            const side = this.safeString (order, 'type');
            if (market === undefined) {
                let marketId = undefined;
                if ('pair' in order) {
                    marketId = order['pair'];
                } else if (('in_currency' in order) && ('out_currency' in order)) {
                    if (side === 'buy') {
                        marketId = order['in_currency'] + '_' + order['out_currency'];
                    } else {
                        marketId = order['out_currency'] + '_' + order['in_currency'];
                    }
                }
                if ((marketId !== undefined) && (marketId in this.markets_by_id)) {
                    market = this.markets_by_id[marketId];
                }
            }
            let amount = this.safeNumber (order, 'quantity');
            if (amount === undefined) {
                const amountField = (side === 'buy') ? 'in_amount' : 'out_amount';
                amount = this.safeNumber (order, amountField);
            }
            let price = this.safeNumber (order, 'price');
            let cost = this.safeNumber (order, 'amount');
            let filled = 0.0;
            const trades = [];
            const transactions = this.safeValue (order, 'trades', []);
            let feeCost = undefined;
            let lastTradeTimestamp = undefined;
            let average = undefined;
            const numTransactions = transactions.length;
            if (numTransactions > 0) {
                feeCost = 0;
                for (let i = 0; i < numTransactions; i++) {
                    const trade = this.parseTrade (transactions[i], market);
                    if (id === undefined) {
                        id = trade['order'];
                    }
                    if (timestamp === undefined) {
                        timestamp = trade['timestamp'];
                    }
                    if (timestamp > trade['timestamp']) {
                        timestamp = trade['timestamp'];
                    }
                    filled = this.sum (filled, trade['amount']);
                    feeCost = this.sum (feeCost, trade['fee']['cost']);
                    trades.push (trade);
                }
                lastTradeTimestamp = trades[numTransactions - 1]['timestamp'];
            }
            let status = this.safeString (order, 'status'); // in case we need to redefine it for canceled orders
            let remaining = undefined;
            if (amount !== undefined) {
                remaining = amount - filled;
                if (filled >= amount) {
                    status = 'closed';
                } else {
                    status = 'open';
                }
            }
            if (market === undefined) {
                market = this.getMarketFromTrades (trades);
            }
            let feeCurrency = undefined;
            if (market !== undefined) {
                symbol = market['symbol'];
                feeCurrency = market['quote'];
            }
            if (cost === undefined) {
                if (price !== undefined) {
                    cost = price * filled;
                }
            } else {
                if (filled > 0) {
                    if (average === undefined) {
                        average = cost / filled;
                    }
                    if (price === undefined) {
                        price = cost / filled;
                    }
                }
            }
            const fee = {
                'cost': feeCost,
                'currency': feeCurrency,
            };
            const clientOrderId = this.safeInteger (order, 'client_id');
            return {
                'id': id,
                'clientOrderId': clientOrderId,
                'datetime': this.iso8601 (timestamp),
                'timestamp': timestamp,
                'lastTradeTimestamp': lastTradeTimestamp,
                'status': status,
                'symbol': symbol,
                'type': 'limit',
                'timeInForce': undefined,
                'postOnly': undefined,
                'side': side,
                'price': price,
                'stopPrice': undefined,
                'cost': cost,
                'amount': amount,
                'filled': filled,
                'remaining': remaining,
                'average': average,
                'trades': trades,
                'fee': fee,
                'info': order,
            };
        }
    
