            const response = await this.privatePostQueryLedgers (request);
            // {  error: [],
            //   result: { 'LPUAIB-TS774-UKHP7X': {   refid: "A2B4HBV-L4MDIE-JU4N3N",
            //                                         time:  1520103488.314,
