            const id = this.safeString (data, 'orderId');
            const order = {
                'id': id,
                'clientOrderId': clientOrderId,
                'info': data,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'lastTradeTimestamp': undefined,
                'symbol': symbol,
                'type': type,
                'side': side,
                'price': price,
                'amount': this.parseNumber (amountString),
                'cost': this.parseNumber (costString),
                'average': undefined,
                'filled': undefined,
                'remaining': undefined,
                'status': undefined,
                'fee': undefined,
                'trades': undefined,
            };
            return order;
