                'info': trade,
                'id': id,
                'order': orderId,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'symbol': symbol,
                'type': type,
                'takerOrMaker': takerOrMaker,
                'side': side,
                'price': price,
                'amount': amount,
