                'id': id,
                'info': transaction,
                'timestamp': timestamp,
                'datetime': this.iso8601 (timestamp),
                'address': address,
                'addressTo': address,
                'addressFrom': undefined,
                'tag': tag,
                'tagTo': tag,
                'tagFrom': undefined,
                'currency': code,
                'amount': amount,
